import{_ as a,c as n,a0 as r,o as t}from"./chunks/framework.CY2JQc0e.js";const u=JSON.parse('{"title":"Veelgestelde vragen","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"nl/faq/index.md","filePath":"nl/faq/index.md"}'),s={name:"nl/faq/index.md"};function l(d,e,i,o,m,c){return t(),n("div",null,e[0]||(e[0]=[r('<h1 id="veelgestelde-vragen" tabindex="-1">Veelgestelde vragen <a class="header-anchor" href="#veelgestelde-vragen" aria-label="Permalink to &quot;Veelgestelde vragen&quot;">​</a></h1><p>Deze pagina bevat de <strong>veelgestelde vragen</strong>, met het bijbehorende antwoord. Als u hier geen antwoord op uw vragen kunt vinden, <a href="./../about/contact/">neem dan gerust contact op met ons</a>.</p><h2 id="algemeen" tabindex="-1">Algemeen <a class="header-anchor" href="#algemeen" aria-label="Permalink to &quot;Algemeen&quot;">​</a></h2><details class="details custom-block"><summary>Wanneer vindt de wedstrijd plaats?</summary><p>In de herfst.</p><p><em>De Bebras-wedstrijd vindt één keer per jaar plaats, meestal in november. De exacte data worden doorgaans in augustus gepubliceerd.</em></p></details><details class="details custom-block"><summary>Waar gaat de wedstrijd door?</summary><p>Op school.</p><p><em>De Bebras-wedstrijd vindt online plaats op de schoolcomputers, onder leiding van een leraar of lerares. Je kan wel thuis (anoniem) <a href="https://bebras.ugent.be/contest/o/index/nl" target="_blank" rel="noreferrer">een Bebras-wedstrijd uitproberen</a>.</em></p></details><details class="details custom-block"><summary>Hoe lang duurt de wedstrijd?</summary><p>40 minuten.</p><p><em>In die tijdspanne, moeten de deelnemers tussen 13 en 16 vragen beantwoorden, afhankelijk van hun categorie, die afhankelijk is van hun schoolniveau.</em></p></details><details class="details custom-block"><summary>Zijn er kosten verbonden aan deelname aan de wedstrijd?</summary><p>Nee.</p><p><em>Deelname aan de Bebras-wedstrijd is volledig gratis.</em></p></details><details class="details custom-block"><summary>Vallen er prijzen te winnen?</summary><p>In Vlaanderen helaas niet.</p><p><em>De 15 beste deelnemers (per leeftijdscategorie) worden opgelijst op deze site. En misschien geeft je school je wel een diploma om boven je bed te hangen?</em></p></details><h2 id="leerkracht" tabindex="-1">Leerkracht <a class="header-anchor" href="#leerkracht" aria-label="Permalink to &quot;Leerkracht&quot;">​</a></h2><details class="details custom-block"><summary>Is er een minimum aantal leerlingen nodig om de wedstrijd to organiseren?</summary><p>Nee, er zijn geen restricties.</p><p><em>Je kan de Bebras-wedstrijd ook organiseren voor leerlingen uit verschillende klassen en/of leeftijden, en die hoeven ook niet allemaal op hetzelfde moment mee te doen.</em></p></details><details class="details custom-block"><summary>Kunnen we meerdere klassen inschrijven?</summary><p>Ja, natuurlijk.</p><p><em>En die hoeven ook niet allemaal op hetzelfde moment mee te doen.</em></p></details><h2 id="leerling" tabindex="-1">Leerling <a class="header-anchor" href="#leerling" aria-label="Permalink to &quot;Leerling&quot;">​</a></h2><details class="details custom-block"><summary>Hoe inschrijven voor de wedstrijd?</summary><p>Via je leraar of lerares.</p><p><em>Vraag dus aan je favoriete leraar of lerares om de Bebras-wedstrijd in jouw klas/school te organiseren, zodat jij mee kunt doen.</em></p></details>',13)]))}const h=a(s,[["render",l]]);export{u as __pageData,h as default};
